for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[4 * 4 + 3 - ib]);
device.lora.value = dataView.getInt32(0);

device.windDirection.value = device.windDirection.value - device.northAngle.value;
if (device.windDirection.value < 0.0) device.windDirection.value = device.windDirection.value + 360.0;

var newSettingDoneMsg = {topic:'newSettingDone', payload:true};
var now = new Date().getTime();
var old = context.get('lastTime');
context.set('lastTime',now);
//node.warn(now - old);

device.windSpeed.value = 0.65 * 0.65 * device.windSpeed.value; //calibration for radius and georg call

var realWind = device.windSpeed.value * Math.cos(device.windDirection.value * 0.017453293);
var imagWind = device.windSpeed.value * Math.sin(device.windDirection.value * 0.017453293);

var windVec1 = context.get('windVec1');
if (windVec1 == undefined) windVec1 = {'re':0.0, 'im':0.0};
var windVec2 = context.get('windVec2');
if (windVec2 == undefined) windVec2 = {'re':0.0, 'im':0.0};
windVec1.re = windVec1.re + (realWind - windVec1.re) / 10.2; //2 minutes for 11.75 secs per sample
windVec1.im = windVec1.im + (imagWind - windVec1.im) / 10.2; //2 minutes for 11.75 secs per sample
windVec2.re = windVec2.re + (realWind - windVec2.re) / 51.0; //10 minutes for 11.75 secs per sample
windVec2.im = windVec2.im + (imagWind - windVec2.im) / 51.0; //10 minutes for 11.75 secs per sample
context.set('windVec1',windVec1);
context.set('windVec2',windVec2);

var windMag1 = context.get('windMag1');
if (windMag1 == undefined) windMag1 = device.windSpeed.value;
var windMag2 = context.get('windMag2');
if (windMag2 == undefined) windMag2 = device.windSpeed.value;
windMag1 = windMag1 + (device.windSpeed.value - windMag1) / 10.2; //2 minutes for 11.75 secs per sample
windMag2 = windMag2 + (device.windSpeed.value - windMag2) / 51.0; //10 minutes for 11.75 secs per sample
context.set('windMag1',windMag1);
context.set('windMag2',windMag2);

device.windSpeed.value = Math.round(device.windSpeed.value * 10) / 10;
device.windSpeed1.value = Math.round(windMag1 * 10) / 10;
device.windSpeed2.value = Math.round(windMag2 * 10) / 10;

device.windDirection1.value = 180.0 * Math.atan2(windVec1.im,windVec1.re) / Math.PI;
if (device.windDirection1.value < 0.0) device.windDirection1.value = device.windDirection1.value + 360.0;
device.windDirection1.value = Math.round(device.windDirection1.value * 10) / 10;

device.windDirection2.value = 180.0 * Math.atan2(windVec2.im,windVec2.re) / Math.PI;
if (device.windDirection2.value < 0.0) device.windDirection2.value = device.windDirection2.value + 360.0;
device.windDirection2.value = Math.round(device.windDirection2.value * 10) / 10;

var windBuffer = context.get('windBuffer');
if (windBuffer == undefined) windBuffer = [];
windBuffer.push(device.windSpeed.value);
if (windBuffer.length > 60) windBuffer.shift();
device.gusts.value = 0;
if (windBuffer.length > 0)
{
    device.gusts.value  = Math.max(...windBuffer);
}
context.set('windBuffer',windBuffer);

return newSettingDoneMsg;

