        function getDirectionText(wd)
        {
            var dirText = '';           
            if ((  0.0 <= wd) && (wd <=  22.5)) dirText = 'N ';
            if (( 22.5 <  wd) && (wd <=  67.5)) dirText = 'NE';
            if (( 67.5 <  wd) && (wd <= 112.5)) dirText = 'E ';
            if ((112.5 <  wd) && (wd <= 157.5)) dirText = 'SE';
            if ((157.5 <  wd) && (wd <= 202.5)) dirText = 'S ';
            if ((202.5 <  wd) && (wd <= 247.5)) dirText = 'SW';
            if ((247.5 <  wd) && (wd <= 292.5)) dirText = 'W ';
            if ((292.5 <  wd) && (wd <= 337.5)) dirText = 'NW';
            if ((337.5 <  wd) && (wd <= 360.0)) dirText = 'N ';
            return dirText;
        }

