        class StripChart
        {
           constructor() 
            {
                this.plotStartTime = new Date().getTime();
                this.plotLength = 600000;
                this.htmlId = 'timeplotStripChart';
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : '500',
                    width       : '',
                    xaxis       :
                    {
//                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                        type: 'date'
                    },
                    yaxis:
                    {
                        title           : 'Power (kW)',
                        titlefont       : {color: '#0000ff'},
                        tickfont        : {color: '#0000ff'},
                        gridcolor       : '#9999ff',
                        zerolinecolor   : '#9999ff',
                        linecolor       : '#9999ff',

                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: 'Forward Power',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000ff'}

                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'Reverse Power',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#00ff00'}
                };
            }
            addTracePoints(trace1Pt, trace2Pt)
            {
//                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var now = new Date().getTime();
                var plotBeginning = now - this.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                Plotly.newPlot(this.htmlId, [this.trace1, this.trace2], this.layout);
            }
        }

