        function getCreds()
        {
            var now = new Date().getTime();
            var expDate = Number(getCookie('ExpDate'));
            if (expDate > 0)
            {
                expDate = expDate - now;
                if(expDate < 0)
                {
                    logout();
                }
                else
                {
                    var hours = Math.floor(expDate / 3600000);
                    var minutes = expDate - hours * 3600000;
                    minutes = Math.floor(minutes / 60000);
                    var seconds = expDate - (hours * 3600000) - (minutes * 60000);
                    seconds = Math.floor(seconds / 1000);
                    hours = hours.toString();
                    if (hours.length == 1) hours = '0' + hours; 
                    minutes = minutes.toString();
                    if (minutes.length == 1) minutes = '0' + minutes; 
                    seconds = seconds.toString();
                    if (seconds.length == 1) seconds = '0' + seconds; 
                    var timeString = hours + ':' + minutes + ':' + seconds;
                    $('#usernameId').text(getCookie('Username'));
                    $('#expTimeId').text(timeString);
                }
            }
            else
            {
                $('#usernameId').text(getCookie('Username'));
                $('#expTimeId').text('Forever');
            }
        }
